import React, { useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight, X, Play, Pause } from 'lucide-react';

const GalleryPage = () => {
  const galleryImages = [
    "/assets/IMG-20250817-WA0002.jpg",
    "/assets/IMG-20250817-WA0004.jpg",
    "/assets/IMG-20250817-WA0005.jpg",
    "/assets/IMG-20250817-WA0006.jpg",
    "/assets/IMG-20250817-WA0008.jpg",
    "/assets/IMG-20250817-WA0009.jpg",
    "/assets/IMG-20250817-WA0010.jpg",
    "/assets/IMG-20250817-WA0011.jpg",
    "/assets/IMG-20250817-WA0012.jpg",
    "/assets/IMG-20250817-WA0013.jpg",
    "/assets/IMG-20250817-WA0014.jpg",
    "/assets/IMG-20250817-WA0015.jpg"
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAutoPlay, setIsAutoPlay] = useState(true);
  const [selectedImage, setSelectedImage] = useState(null);

  // Auto-slide functionality
  useEffect(() => {
    if (isAutoPlay) {
      const interval = setInterval(() => {
        setCurrentIndex((prevIndex) => 
          prevIndex === galleryImages.length - 1 ? 0 : prevIndex + 1
        );
      }, 2000); // 2 seconds

      return () => clearInterval(interval);
    }
  }, [isAutoPlay, galleryImages.length]);

  const nextImage = () => {
    setCurrentIndex(currentIndex === galleryImages.length - 1 ? 0 : currentIndex + 1);
  };

  const prevImage = () => {
    setCurrentIndex(currentIndex === 0 ? galleryImages.length - 1 : currentIndex - 1);
  };

  const openImagePopup = (image) => {
    setSelectedImage(image);
  };

  const closeImagePopup = () => {
    setSelectedImage(null);
  };

  const toggleAutoPlay = () => {
    setIsAutoPlay(!isAutoPlay);
  };

  return (
    <div className="min-h-screen bg-gray-900">
      {/* Hero Section */}
      <section className="relative h-96 flex items-center justify-center overflow-hidden">
        <div 
          className="absolute inset-0 bg-cover bg-center bg-no-repeat"
          style={{ backgroundImage: `url('/assets/IMG-20250817-WA0002.jpg')` }}
        />

        <div className="relative z-10 text-center text-white px-4 sm:px-6 lg:px-8 max-w-4xl mx-auto">
          <h1 className="text-4xl md:text-5xl font-serif font-bold mb-4 text-shadow-lg">
            Farm Gallery
          </h1>
          <p className="text-xl md:text-2xl text-shadow">
            Explore the beauty of Cozy Glory Shed through our photo collection
          </p>
        </div>
      </section>

      {/* Netflix-style Gallery */}
      <section className="py-16 bg-gray-900">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center mb-8">
            <h2 className="text-3xl font-serif font-bold text-white">
              Farm Experience Gallery
            </h2>
            <button
              onClick={toggleAutoPlay}
              className="flex items-center space-x-2 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors duration-200"
            >
              {isAutoPlay ? <Pause size={20} /> : <Play size={20} />}
              <span>{isAutoPlay ? 'Pause' : 'Play'}</span>
            </button>
          </div>

          {/* Main Image Display */}
          <div className="relative mb-8">
            <div className="relative h-96 md:h-[500px] rounded-lg overflow-hidden">
              <img
                src={galleryImages[currentIndex]}
                alt={`Farm view ${currentIndex + 1}`}
                className="w-full h-full object-cover cursor-pointer hover:scale-105 transition-transform duration-300"
                onClick={() => openImagePopup(galleryImages[currentIndex])}
              />

              {/* Navigation Arrows */}
              <button
                onClick={prevImage}
                className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-50 hover:bg-opacity-75 text-white p-2 rounded-full transition-all duration-200"
              >
                <ChevronLeft size={24} />
              </button>
              <button
                onClick={nextImage}
                className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-50 hover:bg-opacity-75 text-white p-2 rounded-full transition-all duration-200"
              >
                <ChevronRight size={24} />
              </button>

              {/* Image Counter */}
              <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 bg-black bg-opacity-50 text-white px-3 py-1 rounded-full text-sm">
                {currentIndex + 1} / {galleryImages.length}
              </div>
            </div>
          </div>

          {/* Thumbnail Strip */}
          <div className="flex space-x-4 overflow-x-auto pb-4">
            {galleryImages.map((image, index) => (
              <div
                key={index}
                className={`flex-shrink-0 cursor-pointer transition-all duration-200 ${
                  index === currentIndex 
                    ? 'ring-4 ring-red-600 scale-105' 
                    : 'hover:scale-105 opacity-70 hover:opacity-100'
                }`}
                onClick={() => setCurrentIndex(index)}
              >
                <img
                  src={image}
                  alt={`Thumbnail ${index + 1}`}
                  className="w-24 h-16 object-cover rounded-lg"
                />
              </div>
            ))}
          </div>

          {/* Grid Gallery */}
          <div className="mt-16">
            <h3 className="text-2xl font-serif font-bold text-white mb-8">
              All Farm Photos
            </h3>
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              {galleryImages.map((image, index) => (
                <div
                  key={index}
                  className="relative group cursor-pointer overflow-hidden rounded-lg"
                  onClick={() => openImagePopup(image)}
                >
                  <img
                    src={image}
                    alt={`Farm photo ${index + 1}`}
                    className="w-full h-48 object-cover transition-transform duration-300 group-hover:scale-110"
                  />
                  <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-30 transition-all duration-300 flex items-center justify-center">
                    <div className="text-white opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                      <span className="text-sm font-medium">Click to view</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Image Popup Modal */}
      {selectedImage && (
        <div className="fixed inset-0 bg-black bg-opacity-90 z-50 flex items-center justify-center p-4">
          <div className="relative max-w-4xl max-h-full">
            <button
              onClick={closeImagePopup}
              className="absolute -top-12 right-0 text-white hover:text-gray-300 transition-colors duration-200"
            >
              <X size={32} />
            </button>
            <img
              src={selectedImage}
              alt="Full size view"
              className="max-w-full max-h-[80vh] object-contain rounded-lg"
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default GalleryPage;
